import React, { useEffect, useState } from 'react';
import "./style/Register.css";
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleExclamation } from '@fortawesome/free-solid-svg-icons'

function Register() {

    useEffect(() => {
        document.getElementById('nav').style.display = "none";
    })

    const clickHandel = ()=>{
        axios.post('http://localhost/BigTest/testo/API/insertUser.php?name=bahaa&email=bahaa@mail.com&pass=123&mobile=07999&birth=1999-06-05')
        .then((data) => {
        })
        .catch((error) => {
            console.error(error);
        });
    }

    const [email, setEmail] = useState('');
    const emailReg = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    const emailChange = (e)=>{
        setEmail(e.target.value);

        if(emailReg.test(email) && email !== ""){
            document.getElementById('mail_warr').style.display = "none";
        }else{
            document.getElementById('mail_warr').style.display = "block";
        }
    }

    const [pass, setPass] = useState('');
    const passReg = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]){8,100}/;

    const passChange = (e)=>{
        setPass(e.target.value);
        console.log(passReg.test(pass) && pass !== "");
        console.log("vali: "+e.target.value);
        console.log("var: "+pass);
        console.log("input: "+pass);

        if(passReg.test(e.target.value) && e.target.value !== ""){
            document.getElementById('pass_warr').style.display = "none";
        }else{
            document.getElementById('pass_warr').style.display = "block";
        }
    }

    return (
        <div style={{ height: "100vh" }}>
            <div style={{ height: "100vh", width: "55%", display: "inline-block", position: "relative" }}>
                <div style={{ width: "fit-content", margin: "auto", display: "inline-block", position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)" }}>

                    <div className='form'>
                        <h3 style={{ textAlign: "center", marginTop: 0 }}>REGISTRATION</h3>
                        <div>
                            <span style={{ display: "inline-block", position: "relative" }}>
                                <label htmlFor="first">first name</label>
                                <div >
                                    <input type="text" id='first' />
                                </div>
                                <span className='warr'><FontAwesomeIcon icon={faCircleExclamation}></FontAwesomeIcon>&nbsp;Only alphabet characters
                                {/* Please enter your name */}
</span>
                            </span>
                            <span style={{ display: "inline-block", marginLeft: "15px" }}>
                                <label htmlFor="middle">middle name</label>
                                <div >
                                    <input type="text" id='middle' />
                                </div>
                            </span>
                        </div>
                        <div className='rows'>
                            <span style={{ display: "inline-block" }}>
                                <label htmlFor="last">last name</label>
                                <div >
                                    <input type="text" id='last' />
                                </div>
                            </span>
                            <span style={{ display: "inline-block", marginLeft: "15px" }}>
                                <label htmlFor="family">family name</label>
                                <div >
                                    <input type="text" id='family' />
                                </div>
                            </span>
                        </div>
                        <div className='rows' style={{ position: "relative" }}>
                            <label htmlFor="email">email</label>
                            <div >
                                <input type="email" id='email' onChange={emailChange} />
                            </div>
                            <span className='warr' id='mail_warr'><FontAwesomeIcon icon={faCircleExclamation}>
                                </FontAwesomeIcon>&nbsp;email is not valid
                            </span>
                        </div>
                        <div className='rows' style={{ position: "relative" }}>
                            <label htmlFor="pass">password</label>
                            <div >
                                <input type="password" id='pass' onChange={passChange} />
                            </div>
                            <span className='warr' id='pass_warr'><FontAwesomeIcon icon={faCircleExclamation}>
                                </FontAwesomeIcon>&nbsp;password is not valid
                            </span>
                        </div>
                        <div className='rows'>
                            <label htmlFor="cpass">confirm password</label>
                            <div >
                                <input type="password" id='cpass' />
                            </div>
                        </div>
                        <div className='rows'>
                            <label htmlFor="mobile">mobile</label>
                            <div >
                                <input type="tel" id='mobile' />
                            </div>
                        </div>
                        <div className='rows'>
                            <label htmlFor="birth">birth date</label>
                            <div >
                                <input type="date" id='birth' />
                            </div>
                        </div>
                        <button onClick={clickHandel}>Sign Up</button>
                    </div>
                    <div style={{ textAlign: "center", padding: "10px" }}>
                        <span>Already have an account? <a href="/Login">Login</a></span>
                    </div>
                </div>
            </div>
            <div style={{ maxHeight: "100vh", width: "45%", display: "inline-block", backgroundImage: "url(https://media.istockphoto.com/vectors/abstract-modern-geometric-retro-background-vector-id1188381613?k=20&m=1188381613&s=612x612&w=0&h=VMCsct3EYvQiif83MzX2W5DYHLhBzbOqRHEfmHHKtqk=)", height: "100vh", position: "relative" }}>
                <div style={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -65%)", width: "fit-content" }}>
                    <h1 style={{ width: "fit-content", color: "#414BB2", backgroundColor: "white", fontSize: "3.5rem", margin: "0 auto 20px" }}>Hello, Friend!</h1>
                    <p style={{ fontSize: "2.2rem", color: "#414BB2", backgroundColor: "#ffffff78", width: "80%", margin: "auto" }}>
                        Enter your personal details and start journy with us
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Register;
